import React, {Component} from 'react';
import './App.css';
import Todos from './components/Todos'
import Header from './components/TodoLayout/Header'

class App extends Component {
  state={
    todos:[
      {
        id: 1,
        title: 'Take out the trash',
        completed: false
      },
      {
        id: 2,
        title: 'Dinner with wife',
        completed: true
      },
      {
        id: 3,
        title: 'Meeting with boss',
        completed: false
      },
    ]
  }

  //Toggle Complete
  markComplete = (id) => {
    this.setState(
      {todos: this.state.todos.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed
        }
        return todo;
      })}
    )
}

delButton = (id) =>
{
  this.setState({todos: [...this.state.todos.filter(todo => todo.id !== id)]});

}

  render() {
    return (
      <div className="App">
        <Header />
        <Todos todos={this.state.todos} markComplete = {this.markComplete} delButton= {this.delButton} />
      </div>
    );
  }
}

export default App;
